import { useState } from "react";
import "./App.css";

export default function App() {

  const InitialCharacters = [
    {
      name: "Walter White",
      poster:
        "data:image/jpeg;base64,",
      summary:
        "Walter is kind, caring and timid, but also demanding, calculating, and ruthless. His invented pseudonym, Heisenberg, becomes an alter ego – a fantasy for him to live out a love of power. Beneath both sides of his dual personality, he views the world in the rarefied scientific terms of chemistry."
      , rating: 9.8
    },
    {
      name: "Jesse Pinkman",
      poster:
        "data:image/jpeg;base64,",
      summary:
        "Jesse Bruce Pinkman is a fictional character in the American television series Breaking Bad, played by Aaron Paul. He is a crystal meth cook and dealer, and works with his former high school chemistry teacher, Walter White (Bryan Cranston), in a meth operation."
      , rating: 9
    },
    {
      name: "Mike Ehrmantraut",
      poster:
        "data:image/jpeg;base64,",
      summary:
        "After a lifetime of violence and crime, Mike is a counter-intuitive combination of world-weary and indefatigable – he isn't proud of the things he's done, but seems to have made his peace with them. Mike is gruff in his working life, but is kind and gentle with innocents, particularly granddaughter Kaylee."
      , rating: 8.5
    },
    {
      name: "Gustavo Fring",
      poster:
        "data:image/jpeg;base64,",
      summary:
        "Gus is a calm, calculating, mysterious character. Despite the fact that he is really a ruthless drug lord and murderer, Gus knows that appearances are important. On the outside, Gus is a professional, compassionate human being. He is an upstanding and likable businessman in the Albuquerque community."
      , rating: 9.5
    }
  ];

const [CharacterList, setCharacterList]= useState(InitialCharacters)

  const[name,setname]=useState("")
  const[moviepos,setmoviepos]=useState("") 
  const[moviesum,setmoviesum]=useState("")
  const[movierat,setmovierat]=useState("")
  return (
    <>
    <div className="formlist">
      <input type="text" name="name" placeholder="name" onChange={(e)=>setname(e.target.value)}/>
      <input type="text" name="poster" placeholder="poster" onChange={(e)=>setmoviepos(e.target.value)}/>
      <input type="text" name="summary" placeholder="summary" onChange={(e)=>setmoviesum(e.target.value)}/>
      <input type="number" name="rating" placeholder="rating" onChange={(e)=>setmovierat(e.target.value)}/>
      <button className="addnew" 
      onClick={()=>{
        const NewChar={
          name: name,
          poster: moviepos,
          summary: moviesum,
          rating: movierat
        }
        setCharacterList([...CharacterList, NewChar])
      }
      }>Add Movie</button>
      </div>
    <div className="App">
    
      {CharacterList.map((char, index) => (
        <Breadkingbad
          key={index}
          name={char.name}
          poster={char.poster}
          summary={char.summary}
          rating={char.rating}
        />
      ))}
    </div>
    {/* <Color/> */}
    </>
  );
}

function Breadkingbad({ name, poster, summary, rating }) {
  const [state, setstate] = useState(0)
  const [dislike, setdislike] = useState(0)
  const styles = {
    color: rating > 8.5 ? "white" : "black"
  }

  return (
    <div className="display">
      <img className="imagedisplay" src={poster} ></img>
      <div className="head">
        <h2 className="name">{name}</h2>
        <p style={styles}>⭐{rating}</p>
      </div>
      <p className=" summ" style={{ backgroundColor: "green" }}>{summary}</p>
      <div className="symbol">
        <button className="thumbs" onClick={() => setstate(state + 1)}>👍{state}</button>
        <button className="down" onClick={() => setdislike(dislike + 1)}>👎{dislike}</button>
      </div>
    </div>
  );
}

function Color() {
  const [color, setcolor] = useState("pink");
  const backcr = {
    backgroundColor: color
  }
  const [colorList, setcolorList] = useState(["red", "blue", "yellow", "black"])
  return (
    <div>
      <input type="text" placeholder="Enter a Color" onChange={(e) => setcolor(e.target.value)} style={backcr} />
      <button onClick={() => setcolorList([...colorList, color])}>Add Color</button>
      {colorList.map((clr) => {
        return (
          <Colorbox color={clr}></Colorbox>
        )
      }
      )}
    </div>
  )
}

function Colorbox({ color }) {
  const colorboxstyle = {
    width: "200px",
    height: "30px",
    margin: "10px",
    backgroundColor: color,
  }
  return (
    <div style={colorboxstyle}>
    </div>

  )
}